import React, { useState, useEffect, useCallback } from "react";
import { PopupWithForm } from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

export function EditProfilePopup(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const [profileFormValues, setProfileFormValues] = useState({
    userName: '',
    userDescription: '',
  });

  const [profileFormValidity, setProfileFormValidity] = useState({
    userNameValid: false,
    userDescriptionValid: false,
  });

  const errors = {
    userName: {
      minLength: true,
      maxLength: true,
    },
    userDescription: {
      minLength: true,
      maxLength: true,
    },
  };

  const handleInputChange = useCallback((evt) => {
    const { name, value } = evt.target;
    setProfileFormValues(prevState => ({ ...prevState, [name]: value }));
  }, [setProfileFormValues]);



  useEffect(() => {
    setProfileFormValues({
      userName: currentUser.name,
      userDescription: currentUser.about,
    })
  }, [currentUser, props.isOpen]);

  function handleSubmit(evt) {
    evt.preventDefault();
    props.onUpdateUser({
      name: userName,
      about: userDescription,
    })
  };

  useEffect(
    function validateInputs() {
    const isUserNameFilled = profileFormValues.userName.length > 2;
    const isUserNameValid = isUserNameFilled;

    const isUserDescriptionFilled = profileFormValues.userDescription.length > 2;
    const isUserDescriptionValid = isUserDescriptionFilled;

    setProfileFormValidity((prevValidity) => ({
      userNameValid: isUserNameValid,
      userDescriptionValid: isUserDescriptionValid,
    }));
  }, 
  [profileFormValues, setProfileFormValidity]);

  const { userName, userDescription } = profileFormValues;
  const { userNameValid, userDescriptionValid } = profileFormValidity;

  return(
    <PopupWithForm name="edit" title="Редактировать профиль" button="Сохранить" 
    isOpen={props.isOpen} 
    onClose={props.onClose}
    onSubmit={handleSubmit} >

      {!userNameValid && <div> userName invalid </div>}

      <input className="popup__input" id="input-name" name="userName" type="text" 
      value={userName} 
      onChange={handleInputChange} 
      required />
      {errors.userName.minLength && <span className="popup__input-error" >Минимальное количество символов 2</span>}
      <span className="popup__input-error" >Максимальное количество символов 40</span>

      {!userDescriptionValid && <div> userDescription invalid </div>}

      <input className="popup__input" minLength="2" maxLength="200" id="input-job" name="userJob" type="text" 
      value={userDescription} 
      onChange={handleInputChange} 
      required />
      <span className="popup__input-error" >Минимальное количество символов 2</span>
      <span className="popup__input-error" >Максимальное количество символов 40</span>
    </PopupWithForm>
  ); 
}
