import React, { useRef } from "react";
import { PopupWithForm } from "./PopupWithForm";

const validators = {
  avatar: {
    required: (value) => { return value === ''; },
    url: (value) => { return value === 'http://' || 'https://'; },
  },
};

export function EditAvatarPopup(props) {
  const avatarRef = useRef();

  const [errors, setErrors] = useState({
    avatar: {
      required: true,
      url: true,
    },
  });

  function handleSubmit(evt) {
    evt.preventDefault();
    props.onUpdateAvatar({
      avatar: avatarRef.current.value,
    })
  }

  useEffect(
    function validateInput() {
      const avatarValidationResult = Object.keys(validators.avatar).map(
        errorKey => {
          const errorResult = validators.avatar[errorKey](avatar);
          return { [errorKey]: errorResult };
        }
      ).reduce((acc, el) => ({...acc, ...el}));

      setErrors({
        avatar: avatarValidationResult,
      });
  }, 
  [avatar, setErrors]);

  return(
    <PopupWithForm name="avatar" title="Обновить аватар" button="Сохранить" 
    isOpen={props.isOpen} 
    onClose={props.onClose}
    onSubmit={handleSubmit} >
      <input className="popup__input" id="input-avatar" name="avatar" type="url" placeholder="Ссылка" ref={avatarRef} required />

      { errors.avatar.required && <div className="popup__input-error" >Заполните это поле.</div> }
      { errors.avatar.url && <div className="popup__input-error" >Введите URL.</div> }
    </PopupWithForm>
  );
}
