import React, { useState, useEffect, useCallback } from "react";
import { PopupWithForm } from "./PopupWithForm";
import { CurrentUserContext } from '../contexts/CurrentUserContext';

const validators = {
  userName: {
    required: (value) => { return value === ''; },
    minLength: (value) => { return value.length < 2; },
    maxLength: (value) => { return value.length > 40; },
  },
  userDescription: {
    required: (value) => { return value === ''; },
    minLength: (value) => { return value.length < 2; },
    maxLength: (value) => { return value.length > 200; },
  },
};

export function EditProfilePopup(props) {
  const currentUser = React.useContext(CurrentUserContext);

  const [profileFormValues, setProfileFormValues] = useState({
    userName: '',
    userDescription: '',
  });
  const { userName, userDescription } = profileFormValues;

  const [profileFormValidity, setProfileFormValidity] = useState({
    userNameValid: false,
    userDescriptionValid: false,
  });

  const errors = {
    userName: {
      required: true,
      minLength: true,
      maxLength: true,
    },
    userDescription: {
      required: true,
      minLength: true,
      maxLength: true,
    },
  };

  const handleInputChange = useCallback((evt) => {
    const { name, value } = evt.target;
    setProfileFormValues(prevState => ({ ...prevState, [name]: value }));
  }, [setProfileFormValues]);



  useEffect(() => {
    setProfileFormValues({
      userName: currentUser.name,
      userDescription: currentUser.about,
    })
  }, [currentUser, props.isOpen]);

  function handleSubmit(evt) {
    evt.preventDefault();
    props.onUpdateUser({
      name: userName,
      about: userDescription,
    })
  };

  useEffect(
    function validateInputs() {
      const userNameValidationResult = Object.keys(validators.userName).map(
        errorKey => {
          const errorResult = validators.userName[errorKey](userName);
          return { [errorKey]: errorResult };
        }
      ).reduce((acc, el) => ({...acc}));

      const userDescriptionValidationResult = Object.keys(validators.userDescription).map(
        errorKey => {
          const errorResult = validators.userDescription[errorKey](userDescription);
          return { [errorKey]: errorResult };
        }
      );

      console.log(userNameValidationResult, userDescriptionValidationResult)
  }, 
  [profileFormValues, setProfileFormValidity]);

  return(
    <PopupWithForm name="edit" title="Редактировать профиль" button="Сохранить" 
    isOpen={props.isOpen} 
    onClose={props.onClose}
    onSubmit={handleSubmit} >

      <input className="popup__input" id="input-name" name="userName" type="text" 
      value={userName} 
      onChange={handleInputChange} />

      { errors.userName.minLength && <span className="popup__input-error" >Заполните это поле.</span> }
      { errors.userName.minLength && <span className="popup__input-error" >Текст должен быть не короче 2 симв.</span> }
      { errors.userName.maxLength && <span className="popup__input-error" >Максимальное количество символов 40</span> }

      <input className="popup__input" id="input-job" name="userDescription" type="text" 
      value={userDescription} 
      onChange={handleInputChange} />

      { errors.userDescription.minLength && <span className="popup__input-error" >Заполните это поле.</span> }
      { errors.userDescription.minLength && <span className="popup__input-error" >Текст должен быть не короче 2 симв. </span> }
      { errors.userDescription.maxLength && <span className="popup__input-error" >Максимальное количество символов 200</span> }
    </PopupWithForm>
  ); 
}
